import React, { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";

const FormComponent = () => {
  const [inputValue, setInputValue] = useState({
    fname: "",
    lname: "",
    email: "",
    mobile: "",
    message: "",
  });

  const [submitting, setSubmitting] = useState(false); // Track if form is being submitted
  const [error, setError] = useState(""); // Track any errors that occur

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prevent double submissions
    if (submitting) return;

    setSubmitting(true);
    setError(""); // Reset previous errors

    try {
      // Send POST request to backend
      const res = await axios.post("https://portfolio-1-n7cc.onrender.com/register", {
        fname: inputValue.fname,
        lname: inputValue.lname,
        email: inputValue.email,
        mobile: inputValue.mobile,
        message: inputValue.message,
      });

      const data = res.data;

      if (res.status === 201) {
        toast.success("Message sent successfully!");
        console.log("Form submitted:", data);
        // Reset form fields
        setInputValue({
          fname: "",
          lname: "",
          email: "",
          mobile: "",
          message: "",
        });
      } else {
        toast.error("Message not sent!");
      }
    } catch (error) {
      console.error("Error sending message:", error);
      setError("An error occurred, please try again later!");
      toast.error("An error occurred!");
    } finally {
      setSubmitting(false); // Re-enable the button
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Form Fields */}
      <input
        type="text"
        value={inputValue.fname}
        onChange={(e) => setInputValue({ ...inputValue, fname: e.target.value })}
      />
      <input
        type="text"
        value={inputValue.lname}
        onChange={(e) => setInputValue({ ...inputValue, lname: e.target.value })}
      />
      <input
        type="email"
        value={inputValue.email}
        onChange={(e) => setInputValue({ ...inputValue, email: e.target.value })}
      />
      <input
        type="text"
        value={inputValue.mobile}
        onChange={(e) => setInputValue({ ...inputValue, mobile: e.target.value })}
      />
      <textarea
        value={inputValue.message}
        onChange={(e) => setInputValue({ ...inputValue, message: e.target.value })}
      />

      {/* Submit Button */}
      <button type="submit" disabled={submitting}>
        {submitting ? "Submitting..." : "Submit"}
      </button>

      {error && <p style={{ color: "red" }}>{error}</p>}
    </form>
  );
};

export default FormComponent;
