import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import projectData from './data';
import Spinner from 'react-bootstrap/Spinner';
import './Playlist.css';

const Playlist = () => {
  const [spin, setSpin] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => setSpin(false), 1500);
    return () => clearTimeout(timer);
  }, []);

  return (
    <>
      {
        spin ? (
          <div className='d-flex justify-content-center align-items-center' style={{ height: "90vh" }}>
            <Spinner animation="border" variant="danger" /> &nbsp;&nbsp; Loading .....
          </div>
        ) : (
          <div className="container playlist-wrapper">
            <h2 className='text-center mt-4 mb-4'>Projects</h2>
            <div className="row">
              {
                projectData.map((el, index) => (
                  <div className="col-md-4 col-sm-6 col-12 d-flex justify-content-center mb-4" key={index}>
                    <Card className="custom-card">
                      <Card.Img variant="top" className="project-img" src={el.imgsrc} />
                      <Card.Body className='d-flex flex-column align-items-center'>
                        <Card.Title className='text-center'>{el.projectName}</Card.Title>
                        <Card.Text className='text-center'>{el.description}</Card.Text>
                        <Button variant="primary">
                          <a href={el.demo} target="_blank" rel="noopener noreferrer" className="text-decoration-none text-light">
                            Live Demo
                          </a>
                        </Button>
                      </Card.Body>
                    </Card>
                  </div>
                ))
              }
            </div>
          </div>
        )
      }
    </>
  );
};

export default Playlist;
