import React, { useState, useEffect } from "react";
import "./Contact.css";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import AOS from "aos";
import "aos/dist/aos.css";
// Optional: Toast for notifications
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Contact = () => {
  const [inputValue, setInputValue] = useState({
    fname: "",
    Lname: "",
    Email: "",
    Mobile: "",
    message: "",
  });

  useEffect(() => {
    AOS.init({ duration: 1000 });
  }, []);

  const getValue = (e) => {
    const { name, value } = e.target;
    setInputValue((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const { fname, Lname, Email, Mobile, message } = inputValue;

    if (!fname || !Lname || !Email || !Mobile || !message) {
      toast.error("Please fill in all fields!");
      return;
    }

    // Simulated form submission
    toast.success("Message sent successfully!");
    console.log("Form submitted:", inputValue);

    // Reset form
    setInputValue({
      fname: "",
      Lname: "",
      Email: "",
      Mobile: "",
      message: "",
    });
  };

  return (
    <>
      <div className="container mb-5 contact" data-aos="fade-up">
        <h2 className="text-center mb-4">ðŸ“¬ Contact Us</h2>
        <div className="container">
          <Form className="row" onSubmit={handleSubmit}>
            <Form.Group className="mb-3 col-lg-6">
              <Form.Label>First Name</Form.Label>
              <Form.Control
                type="text"
                name="fname"
                value={inputValue.fname}
                onChange={getValue}
                placeholder="Enter your first name"
              />
            </Form.Group>

            <Form.Group className="mb-3 col-lg-6">
              <Form.Label>Last Name</Form.Label>
              <Form.Control
                type="text"
                name="Lname"
                value={inputValue.Lname}
                onChange={getValue}
                placeholder="Enter your last name"
              />
            </Form.Group>

            <Form.Group className="mb-3 col-lg-6">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                name="Email"
                value={inputValue.Email}
                onChange={getValue}
                placeholder="example@example.com"
              />
            </Form.Group>

            <Form.Group className="mb-3 col-lg-6">
              <Form.Label>Mobile</Form.Label>
              <Form.Control
                type="text"
                name="Mobile"
                value={inputValue.Mobile}
                onChange={getValue}
                placeholder="Enter your mobile number"
              />
            </Form.Group>

            <Form.Group className="mb-4 col-12">
              <Form.Label>Message</Form.Label>
              <Form.Control
                as="textarea"
                rows={4}
                name="message"
                value={inputValue.message}
                onChange={getValue}
                placeholder="Write your message here"
              />
            </Form.Group>

            <div className="d-flex justify-content-center">
              <Button variant="primary" className="col-lg-6" type="submit">
                Submit
              </Button>
            </div>
          </Form>
        </div>
        <ToastContainer />
      </div>
    </>
  );
};

export default Contact;
