import React from "react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import Button from "react-bootstrap/Button";
import { NavLink } from "react-router-dom";

const Header = () => {
  const common = {
    marginRight: "15px",
    fontSize: "17px",
    letterSpacing: ".5px",
    color: "#333",
    transition: "color 0.3s ease",
  };

  return (
    <>
      <Navbar bg="light" expand="lg" data-bs-theme="light">
        <Container>
          <Navbar.Brand href="#home" style={{ color: "#6c63ff", fontWeight: "bold" }}>
            <h3>Satyam Kumar</h3>
          </Navbar.Brand>

          {/* Toggle button for smaller screens */}
          <Navbar.Toggle aria-controls="navbar-nav" />

          <Navbar.Collapse id="navbar-nav">
            <Nav className="ms-auto d-flex align-items-center"> {/* Added d-flex and align-items-center */}
              <NavLink
                to="/"
                className="text-decoration-none"
                style={({ isActive }) => ({
                  ...common,
                  color: isActive ? "#6c63ff" : "#333",
                })}
              >
                Home
              </NavLink>

              <NavLink
                to="/About"
                className="text-decoration-none"
                style={({ isActive }) => ({
                  ...common,
                  color: isActive ? "#6c63ff" : "#333",
                })}
              >
                About
              </NavLink>

              <NavLink
                to="/playlist"
                className="text-decoration-none"
                style={({ isActive }) => ({
                  ...common,
                  color: isActive ? "#6c63ff" : "#333",
                })}
              >
                Playlist
              </NavLink>

              <NavLink
                to="/Contact"
                className="text-decoration-none"
                style={({ isActive }) => ({
                  ...common,
                  color: isActive ? "#6c63ff" : "#333",
                })}
              >
                Contact
              </NavLink>

              {/* Resume Button */}
              <Button variant="danger" className="ms-3">
                Resume
              </Button>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    </>
  );
};

export default Header;
