import React from "react";
import Button from 'react-bootstrap/Button';
import "./Home.css";

const Home = () => {
  return (
    <>
      <div className="container home_container py-5">
        <div className="home_innerdiv d-flex flex-column flex-md-row align-items-center justify-content-between gap-4">

          {/* Left Side */}
          <div className="left_div">
            <h2 className="mb-3">
              Hello, I'm <span style={{ color: "#6c63ff" }}>Satyam Kumar</span>
            </h2>
            <h5 style={{ color: "#2f2d69" }}>MERN Stack Developer | Backend Specialist</h5>
            <p style={{ color: "#666", letterSpacing: ".5px", marginTop: "12px" }}>
              I build full-stack web applications using the MERN stack. Passionate about clean code,
              scalable backend systems, and solving real-world problems through technology.
            </p>

            {/* Buttons */}
            <div className="btn_div mt-4 d-flex gap-3">
              <Button 
                style={{ letterSpacing: "1px", border: "none", borderRadius: 4, background: "#2f2d69" }}
                href="/Satyam_Resume.pdf"
                target="_blank"
              >
                Download Resume
              </Button>
              <Button 
                style={{ letterSpacing: "1px", border: "none", borderRadius: 4, background: "#6c63ff" }}
                href="/Contact"
              >
                Let's Connect
              </Button>
            </div>
          </div>

          {/* Right Side */}
          <div className="right_div">
            <img src="undraw_programmer_raqr.png" alt="developer" className="home_img img-fluid" />
          </div>
        </div>
      </div>
    </>
  );
};

export default Home;
